const search = (name, persons) => {
    if (name === '') {
      return (
        persons.map(person =>
          <li key={person.id}>{person.name}: {person.number}</li>)
      );
    } else {
      return (
        persons
        .filter(person => person.name.toLowerCase().startsWith(name.toLowerCase()))
        .map(person =>
          <li key={person.id}>{person.name}: {person.number}</li>)
      );
    }
  }

  export default search;
