import React, { useState } from 'react';

const App = () => {
  const [ persons, setPersons ] = useState([]);
  const [ newName, setNewName ] = useState('');

  const addName = (event) => {
    event.preventDefault();

    if (persons.name.includes(event.target.value)) {
      window.alert("A person already exists with this name");
      setNewName('');
    } else {
      const person = {
        id: persons.length + 1,
        name: newName,
        number: 0,
      }
      setPersons(persons.concat(person));
      setNewName('');
    }
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value);
  }

  return (
    <div>

      <h2>Phonebook</h2>

      <form onSubmit={addName}>
        <div>
          Name: <input 
            value={newName} 
            onChange={handleNameChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>

      <h2>Numbers</h2>
      <ul>
        {persons.map(person =>
          <li>{person.name}: {person.number}</li>
        )}
      </ul>
    </div>
  );
}

export default App;
