import Contact from "./Contact";

const ContactList = (props) => {

	const search = (nameToSearch, persons) => {
		if (nameToSearch === '') {
		  return (
			persons
				.sort()
				.map(person =>
					<Contact key={person.id} id={person.id} name={person.name} number={person.number} persons={props.persons} setPersons={props.setPersons}/>)
		  );
		} else {
		  return (
			persons
				.filter(person => person.name.toLowerCase().startsWith(nameToSearch.toLowerCase()))
				.sort((a, b) => {
					let fa = a.name.toLowerCase(),
        			fb = b.name.toLowerCase();
					if (fa < fb) return -1;
					if (fa > fb) return 1;
					return 0;
				})
				.map(person =>
					<Contact key={person.id} id={person.id} name={person.name} number={person.number} persons={props.persons} setPersons={props.setPersons}/>)
		  );
		}
	  }

	return (
		<div>
			<form onSubmit={props.action}> 
				<div>Search by name: <input 
					value={props.name}
					onChange={props.nameHandler} />
				</div>
			</form>

			<ul>
				{props.name !== '' ? search(props.name, props.persons) : search('', props.persons)}
			</ul>
		</div>
	)
}

export default ContactList;
