import React from 'react';

const Notification = props => {

	if (message === null) return null;

	const spanStyle = {
		color: 'green',
		background: 'lightgrey',
  		fontSize: 20,
  		borderStyle: 'solid',
  		borderRadius: 5,
  		padding: 10,
  		marginBottom: 10
	}

	return (
		<div style={spanStyle}>{props.message}</div>
	)
}

export default Notification;
