import personService from '../services/persons';

const Contact = (props) => {

	const removePerson = id => {
		personService
			.remove(props.id)
			.then(returnedPerson => {
				setPersons(props.persons.filter(person => person !== returnedPerson))
			});
	}

	return (
		<li>
			{props.name}: {props.number}
			<button
				onClick={removePerson}>
				delete
			</button>
		</li>
	);
}

export default Contact;
